css:
 .product__buy button {
color: #ffffff;
background-color: #5f9903;
border-color: #5f9903;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
.product__payment.card.mb-3 {
display: none
}
.category-retail .product-card__name{
  display: flex !important;
  justify-content: center !important;
}

.category-retail .prices.product-card__prices{
  display: none !important;
}
.header {
  background: #000 !important;
}
.header__toggler-icon {
  color: #fff !important;
}
#cart-button .fa-shopping-bag::before, #user-button .fa-user::before {
  background-color: #fff !important;;
}
#cart-count {
  background: transparent
}
.fa-search{
 color: #fff !important;
}
.app__header, .app__contact span {
  background: #000;
  color: #fff !important;
}
.minicart__aside .minicart__subtotal:after {
  content: "ATENÇÃO: Descontos promocionais do atacado serão aplicados automaticamente na hora do pagamento do pedido.";
  font-weight: bold;
  color: red
}
.footer .col-12.col-sm-6.col-lg:nth-child(3) {
  display: none !important;
}
