css:
 .product__buy button {
color: #ffffff;
background-color: #5f9903;
border-color: #5f9903;
width: 100%;
}
.product__buy button:hover {
background: #4e7d02;
border-color: #4e7d02;
}
.product__payment.card.mb-3 {
display: none
}
.category-retail .product-card__name{
  display: flex !important;
  justify-content: center !important;
}

.category-retail .prices.product-card__prices{
  display: none !important;
}
.header {
  background: #000 !important;
}
.header__toggler-icon {
  color: #fff !important;
}
#cart-button .fa-shopping-bag::before, #user-button .fa-user::before {
  background-color: #fff !important;;
}
#cart-count {
  background: transparent
}
.fa-search{
 color: #fff !important;
}
.app__header, .app__contact span {
  background: #000;
  color: #fff !important;
}

.footer .col-12.col-sm-6.col-lg:nth-child(3) {
  display: none !important;
}
.cookieconsent {
  background-color: #373737e8;
}
.cookieconsent .text::before {
  content: 'Cookies: a gente guarda estatísticas de visitas para melhorar sua experiência de navegação, saiba mais em nossa ';
  font-size: 13px;
}
.cookieconsent .text {
  color: #fff;
  font-size: 0px;
}
.cookieconsent .text a {
  font-size: 13px;
  color: #FCD000;
}

.cookieconsent button {
  color: #fff;
  border: 1px solid #fff;
}

@media (max-width: 991.98px) {
  .cookieconsent {
    top: 90px;
    bottom: auto;
  }
}
